import React, { Component } from "react";
import './Main.css'

import { FaPlus } from 'react-icons/fa'

//Components statefull precisam do metodo render
export default class Main extends Component {

    // constructor(props) {
    //     super(props);

    //     this.state = {
    //         novaTarefa: '',
    //     }

    //     this.handleInput = this.handleInput.bind(this)
    // }

    //Class fields
    state = {
        novaTarefa: '',
        tarefas: [
            'Fazer Café',
            'beber água',
            'Estudar',
        ],
    }

    handleInput = (text) => {
        this.setState({
            novaTarefa: text
        })
    }

    render() {

        const { novaTarefa, tarefas } = this.state

        return (
            <div className="main">
                <h1>Lista de Tarefas</h1>

                <form action="#" className="form">
                    <input type="text" value={novaTarefa} onChange={(e) => this.handleInput(e.target.value)} />

                    <button type="submit"><FaPlus size={14}/></button>
                </form>

                <ul className="tarefas">
                    {tarefas.map((tarefa) => (
                        <li key={tarefa} className="tarefa">{tarefa}</li>
                    ))}
                </ul>

            </div>
        )
    }
}
